import React from 'react'

import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';

import { styled } from '@mui/material/styles';
import Collapse from '@mui/material/Collapse';
import IconButton from '@mui/material/IconButton';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';

import { Outlet } from "react-router-dom";
import { CardActions } from '@mui/material';

const ExpandMore = styled((props) => {
    const { expand, ...other } = props;
    return <IconButton {...other} />;
})(({ theme, expand }) => ({
    transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
        duration: theme.transitions.duration.shortest,
    }),
}));

const cardContentMain = {
    p: '10px 50px 10px 50px',
    m: '10px 50px 10px 50px',
    bgcolor: '#8D9AA5',
    borderRadius: 5,
}
const cardContentChild = { 
    m: '10px 60px 10px 60px', 
    p: '10px 50px 10px 50px', 
    bgcolor: '#e6e6e6', 
    borderRadius: 5 
}
const CardTemplate = ({ topics }) => {
    const [expanded, setExpanded] = React.useState(false);

    const handleExpandClick = () => {
        setExpanded(!expanded);
    };
    return (
        <>
            <Box sx={{
                textAlign: 'left',
            }}>
                <Card>
                    <CardContent sx={cardContentMain}>
                        <Typography key={topics.id}>
                            <h3>{topics.title}</h3>
                            <p>{topics.desc}</p>
                        </Typography>
                        <CardActions>
                            <Button><ExpandMore
                                expand={expanded}
                                onClick={handleExpandClick}
                                aria-expanded={expanded}
                            >
                                <ExpandMoreIcon />
                            </ExpandMore></Button>
                        </CardActions>
                    </CardContent>

                    <Collapse in={expanded} timeout="auto" unmountOnExit>
                        {topics.subtopics.map((data => {
                            return (
                                <CardContent key={data.id} sx={cardContentChild}>
                                    <Typography>
                                        <h3>{data.title}</h3>
                                        <p>{data.desc}</p>
                                    </Typography>
                                </CardContent>
                            )
                        }))}
                    </Collapse>
                </Card>
            </Box>
            <Outlet />
        </>
    )
}

export default CardTemplate
